import axios from "axios";
import { useEffect, useState } from "react";

export default function ListPertanyaan() {
  const initialFormData = {
    gender: 0,
    gdp: 0,
    gd2p: 0,
    tekanandarah: 0,
    usia: 0,
  };

  const [formData, setFormData] = useState(initialFormData);
  const [result, setResult] = useState("");
  const [saran, setSaran] = useState("");

  useEffect(() => {
    if (result === "Diabetes Mellitus tipe 1") {
      setSaran("Disarankan untuk meminum air putih dan rutin berolahraga.");
    } else if (result === "Diabetes Mellitus tipe 2") {
      setSaran(
        "Disarankan untuk berkonsultasi ke dokter dan menjaga pola makan."
      );
    }
  }, [result]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleGenderChange = (event) => {
    setFormData({
      ...formData,
      gender: parseInt(event.target.value, 10),
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post(
        "http://localhost:5000/predict",
        formData
      );
      setResult(response.data.result);
    } catch (error) {
      console.error("Error:", error);
    }
  };

  const [isOpen, setIsOpen] = useState(false);

  const openModal = () => {
    setIsOpen(true);
  };

  const closeModal = () => {
    setIsOpen(false);
    window.location.reload();
  };

  const handleClearForm = () => {
    setFormData(initialFormData);
  };
  return (
    <>
      {/* MODAL SETELAH BUTTON ANALISA DI CLICK */}
      <Transition appear show={isOpen} as={React.Fragment}>
        <dialog id="my_modal_3" className="modal">
          <div className="modal-box w-9/12 max-w-5xl">
            <form method="dialog">
              <button className="btn btn-sm btn-circle btn-ghost absolute right-2 top-2 border-black">
                ✕
              </button>
            </form>
            <h3 className="font-bold text-lg">Hasil Analisa Sistem</h3>
            <p className="mt-5 leading-8">
              Anda didiagnosa oleh sistem sebagai penderita penyakit {result}.
              Segera berkonsultasi dan hubungi dokter penyakit dalam di rumah
              sakit terdekat anda.
            </p>
            <p className="mt-5 leading-8">{saran}</p>
          </div>
        </dialog>
      </Transition>
      <div className="flex items-center justify-center h-screen bg-gradient-to-r from-[#ff6b6b] to-[#1eae98]">
        <form
          className="bg-white p-12 rounded-lg shadow-md max-w-6xl"
          onSubmit={handleSubmit}
        >
          <h1 className="text-4xl font-bebas mb-4 text-center">
            Anda diharapkan mengisi data-data yang ada dibawah ini!
          </h1>
          <div className="grid grid-cols-3 gap-3">
            {/* JENIS KELAMIN */}
            <div
              className="form-control w-full max-w-xs"
              type="number"
              name="gender"
              value={formData.gender}
              onChange={handleGenderChange}
            >
              <label className="label">
                <span className="label-text">Pilih jenis kelamin</span>
              </label>
              <select className="select select-bordered">
                <option value="" disabled>
                  Pilih Jenis Kelamin
                </option>
                <option value="0">Pria</option>
                <option value="1">Wanita</option>
              </select>
            </div>
            {/* GULA DARAH PUASA */}
            <div className="form-control w-full max-w-xs">
              <label className="label">
                <span className="label-text">
                  Masukkan nilai gula darah puasa anda
                </span>
              </label>
              <input
                type="number"
                name="gdp"
                value={formData.gdp}
                onChange={handleChange}
                placeholder="Ketik disini"
                className="input input-bordered w-full max-w-xs"
              />
              <label className="label"></label>
            </div>
            {/* GULA DARAH 2 JAM */}
            <div className="form-control w-full max-w-xs">
              <label className="label">
                <span className="label-text">
                  Masukkan nilai gula darah 2 jam PP anda
                </span>
              </label>
              <input
                type="number"
                name="gd2p"
                value={formData.gd2p}
                onChange={handleChange}
                placeholder="Ketik disini"
                className="input input-bordered w-full max-w-xs"
              />
              <label className="label"></label>
            </div>
          </div>
          <div className="flex justify-center">
            {/* TEKANAN DARAH */}
            <div className="form-control w-full max-w-xs mr-3">
              <label className="label">
                <span className="label-text">
                  Masukkan nilai tekanan darah anda
                </span>
              </label>
              <input
                type="number"
                name="tekanandarah"
                value={formData.tekanandarah}
                onChange={handleChange}
                placeholder="Ketik disini"
                className="input input-bordered w-full max-w-xs"
              />
              <label className="label"></label>
            </div>
            {/* GULA DARAH 2 JAM */}
            <div className="form-control w-full max-w-xs">
              <label className="label">
                <span className="label-text">Masukkan usia anda</span>
              </label>
              <input
                type="number"
                name="usia"
                value={formData.usia}
                onChange={handleChange}
                placeholder="Ketik disini"
                className="input input-bordered w-full max-w-xs"
              />
              <label className="label"></label>
            </div>
          </div>
          {/* BUTTONS */}
          <div className="font-poppins text-lg mt-4 flex justify-center">
            {/* BUTTON BERSIHKAN FORM */}
            <button
              type="button"
              onClick={handleClearForm}
              className="btn btn-active btn-secondary  mr-3"
            >
              BERSIHKAN FORM
            </button>
            {/* BUTTON ANALISA */}
            <button
              type="submit"
              className="btn btn-active btn-primary"
              onClick={openModal}
            >
              ANALISA
            </button>
          </div>
        </form>
      </div>
    </>
  );
}
