import axios from "axios";
import { useEffect, useState } from "react";
import { Helmet } from "react-helmet";

export default function ListPertanyaan() {
  const initialFormData = {
    gender: 1,
    gdp: 0,
    gd2p: 0,
    tekanandarah: 0,
    usia: 0,
  };

  const [formData, setFormData] = useState(initialFormData);
  const [result, setResult] = useState("");
  const [saran, setSaran] = useState([]);
  const [isButtonDisabled, setIsButtonDisabled] = useState(true);

  useEffect(() => {
    const storedFormData = localStorage.getItem("formData");
    if (storedFormData) {
      setFormData(JSON.parse(storedFormData));
    }
  }, []);

  useEffect(() => {
    localStorage.setItem("formData", JSON.stringify(formData));
    const isAnyInputEmpty = Object.values(formData).some(
      (value) => value === 0 || value === ""
    );
    const isGenderNotSelected = formData.gender === "0";
    setIsButtonDisabled(isAnyInputEmpty || isGenderNotSelected);
  }, [formData]);

  useEffect(() => {
    if (result === "Diabetes Mellitus tipe 1") {
      setSaran([
        "Segera berkonsultasi ke dokter poli penyakit dalam.",
        "Melakukan terapi insulin.",
      ]);
    } else if (result === "Diabetes Mellitus tipe 2") {
      setSaran([
        "Melakukan diet.",
        "Menjaga pola makan dengan mengonsumsi makanan berserat tinggi.",
        "Memperbaiki pola gaya hidup.",
        "Sering berolahraga dan melakukan aktifitas fisik.",
        "Mengurangi kebiasaan merokok",
        "Mengurangi konsumsi minuman manis",
      ]);
    }
  }, [result]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleGenderChange = (event) => {
    setFormData({
      ...formData,
      gender: event.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post(
        "http://api.sispakde.dcinfor.org:8000",
        formData
      );
      setResult(response.data.result);
    } catch (error) {
      console.error("Error:", error);
    }
  };

  const handleClearForm = () => {
    setFormData(initialFormData);
  };
  return (
    <>
      <Helmet>
        <title>List Pertanyaan</title>
      </Helmet>
      {/* MODAL SETELAH BUTTON ANALISA DI CLICK */}
      <dialog dialog id="my_modal_3" className="modal">
        <div className="modal-box w-9/12 max-w-5xl">
          <form method="dialog">
            <button
              onClick={() => {
                window.location.reload();
              }}
              className="btn btn-sm btn-circle btn-ghost absolute right-2 top-2 border-black"
            >
              ✕
            </button>
          </form>
          <h3 className="font-bold text-lg">Hasil Analisa Sistem</h3>
          <p className="mt-5 leading-8">
            Anda didiagnosa oleh sistem sebagai penderita penyakit{" "}
            <b>{result}</b>. Segera berkonsultasi dan hubungi dokter poli
            penyakit dalam di rumah sakit terdekat anda. Atau anda bisa
            berkonsultasi dengan dokter poli penyakit dalam di Rumah Sakit
            Gotong Royong dengan mengakses portal link berikut:{" "}
            <a
              href="https://www.antrianrsgotongroyong.com/"
              target="_blank"
              rel="noopener noreferrer"
              className="text-blue-400"
            >
              <u>https://www.antrianrsgotongroyong.com/</u>
            </a>
          </p>
          <div className="mt-5 leading-8">
            <h2>Saran untuk anda:</h2>
            <ul className="list-decimal">
              {saran.map((item, index) => (
                <li className="ml-5" key={index}>
                  {item}
                </li>
              ))}
            </ul>
          </div>
        </div>
      </dialog>
      <div className="flex flex-col items-center justify-center min-h-screen bg-gradient-to-r from-[#ff6b6b] to-[#1eae98]">
        <form
          className="bg-white p-4 md:p-6 rounded-lg shadow-md w-full max-w-xl m-4 flex flex-col"
          onSubmit={handleSubmit}
        >
          <h1 className="text-2xl md:text-4xl font-bebas mb-4 text-center">
            Anda diharapkan mengisi data-data yang ada dibawah ini!
          </h1>
          {/* JENIS KELAMIN */}
          <div className="form-control mb-4">
            <label className="label">
              <span className="label-text">Pilih jenis kelamin</span>
            </label>
            <select
              className="select select-bordered w-full"
              name="gender"
              value={formData.gender}
              onChange={handleGenderChange}
            >
              <option value="" disabled>
                Pilih Jenis Kelamin
              </option>
              <option value="1">Pria</option>
              <option value="2">Wanita</option>
            </select>
          </div>
          {/* GULA DARAH PUASA */}
          <div className="form-control mb-4">
            <label className="label">
              <span className="label-text">
                Masukkan nilai gula darah puasa anda
              </span>
            </label>
            <input
              type="number"
              name="gdp"
              value={formData.gdp}
              onChange={handleChange}
              placeholder="Ketik disini"
              className="input input-bordered w-full"
              min="50"
              max="400"
            />
            <label className="label"></label>
          </div>
          {/* GULA DARAH 2 JAM */}
          <div className="form-control mb-4">
            <label className="label">
              <span className="label-text">
                Masukkan nilai gula darah 2 jam PP anda
              </span>
            </label>
            <input
              type="number"
              name="gd2p"
              value={formData.gd2p}
              onChange={handleChange}
              placeholder="Ketik disini"
              className="input input-bordered w-full"
              min="70"
              max="400"
            />
            <label className="label"></label>
          </div>
          {/* TEKANAN DARAH */}
          <div className="form-control mb-4">
            <label className="label">
              <span className="label-text">
                Masukkan nilai tekanan darah anda
              </span>
            </label>
            <input
              type="number"
              name="tekanandarah"
              value={formData.tekanandarah}
              onChange={handleChange}
              placeholder="Ketik disini"
              className="input input-bordered w-full"
              min="70"
              max="220"
            />
            <label className="label"></label>
          </div>
          {/* USIA */}
          <div className="form-control mb-4">
            <label className="label">
              <span className="label-text">Masukkan usia anda</span>
            </label>
            <input
              type="number"
              name="usia"
              value={formData.usia}
              onChange={handleChange}
              placeholder="Ketik disini"
              className="input input-bordered w-full"
              min="25"
              max="100"
            />
            <label className="label"></label>
          </div>

          {/* BUTTONS */}
          <div className="font-poppins text-lg mt-4 flex flex-col items-center md:items-stretch">
            {/* BUTTON BERSIHKAN FORM */}
            <button
              type="button"
              onClick={handleClearForm}
              className="btn btn-active btn-secondary mb-3 w-full md:w-auto"
            >
              BERSIHKAN FORM
            </button>

            {/* BUTTON ANALISA */}
            <button
              type="submit"
              className="btn btn-active btn-primary w-full md:w-auto"
              onClick={() => {
                if (isButtonDisabled) {
                  alert("Mohon isi semua nilai sebelum menganalisa.");
                } else if (
                  formData.gdp < 50 ||
                  formData.gdp > 400 ||
                  formData.gd2p < 70 ||
                  formData.gd2p > 400 ||
                  formData.tekanandarah < 80 ||
